import { Component } from '@angular/core';
import Chart from "chart.js/auto";
import Utils from "chart.js/auto";



@Component({
  selector: 'app-tsplot',
  templateUrl: './tsplot.component.html',
  styleUrls: ['./tsplot.component.scss']
})
export class TsplotComponent {


  chart: any = [];
  constructor() {}


  ngOnInit() {

    this.chart = new Chart('tsplot', {
    data: {
        labels: ['January', 'February', 'March', 'April'],
        datasets: [{
            type: 'bar',
            label: 'Eurozone',
            data: [10, 20, 30, 40],
            order: 2,
            borderWidth: 2,
            borderColor: "black",
            backgroundColor: "blue",
            borderSkipped: false,
        },
        {
            type: 'bar',
            label: 'USA',
            data: [-10, -20, 30, 40],
            order: 2,
            borderWidth: 2,
            borderColor: "black",
            backgroundColor: "green",
            borderSkipped: false
        },
        {
            type: 'line',
            label: 'Welt',
            data: [10, 20, 30, 40],
            order: 1,
            borderWidth: 2,
            borderColor: "red",
            backgroundColor: "red",
            pointRadius: 0
        },
        {
            type: 'line',
            label: 'Finanzkrise',
            data: [8, 1, 50, 2],
            order: 1,
            borderWidth: 2,
            borderColor: "yellow",
            backgroundColor: "yellow",
            pointRadius: 0
        }]
    }
  });

  }

}
<p>tsplot works!</p>

<div>
  <canvas id="tsplot">{{chart}}</canvas>
</div>
